# Split the data into individual codes
patients = data.split()

# Create directories
base_path = "./patient_folders"  # Change this to your desired location

# Create the base directory if it doesn't exist
os.makedirs(base_path, exist_ok=True)

# Counter for created folders
created_count = 0

for patient in patients:
    patient_folder = os.path.join(base_path, patient)
    try:
        os.makedirs(patient_folder, exist_ok=True)
        created_count += 1
    except Exception as e:
        print(f"Error creating folder for {patient}: {e}")

print(f"Created {created_count} patient folders in '{base_path}'")
